                <div class="follower-count">70.4K</div>
                <div class="growth-indicator">+23.7%</div>
            </div>
        </div>

        <div class="charts-container">
            <div class="chart-card">
                <div class="chart-title">Engagement Rate by Platform</div>
                <div class="engagement-bars">
                    <div class="engagement-bar bar-instagram">
                        <div class="bar-value">8.3%</div>
                        <div class="bar-label">Instagram</div>
                    </div>
                    <div class="engagement-bar bar-facebook">
                        <div class="bar-value">6.2%</div>
                        <div class="bar-label">Facebook</div>
                    </div>
                    <div class="engagement-bar bar-twitter">
