                        <defs>
                            <linearGradient id="growthGradient" x1="0%" y1="0%" x2="0%" y2="100%">
                                <stop offset="0%" style="stop-color:#00ff7f;stop-opacity:0.4" />
                                <stop offset="100%" style="stop-color:#00ff7f;stop-opacity:0" />
