            transform: translate(-50%, -50%);
            font-size: 48px;
            font-weight: 900;
            color: #00ffff;
            text-shadow: 0 0 20px rgba(0, 255, 255, 0.8);
            z-index: 15;
            background: rgba(0, 0, 0, 0.8);
            padding: 20px;
            border-radius: 50%;
            border: 2px solid #00ffff;
        }

        /* Split Arena Layout */
        .battle-arena {
            display: grid;
            grid-template-columns: 1fr 1fr;
            min-height: calc(100vh - 140px);
            position: relative;
            z-index: 10;
        }

        /* Competitor Sides */
        .competitor {
            padding: 40px;
            position: relative;
            overflow: hidden;
        }

        .competitor-left {
            background: linear-gradient(135deg, rgba(0, 255, 255, 0.1) 0%, rgba(0, 255, 255, 0.05) 100%);
            border-right: 1px solid rgba(0, 255, 255, 0.3);
        }

        .competitor-right {
            background: linear-gradient(135deg, rgba(255, 0, 128, 0.1) 0%, rgba(255, 0, 128, 0.05) 100%);
            border-left: 1px solid rgba(255, 0, 128, 0.3);
        }

        .competitor::before {
            content: '';
            position: absolute;
            top: 0;
            bottom: 0;
            width: 2px;
            background: linear-gradient(to bottom, transparent, rgba(255, 255, 255, 0.1), transparent);
        }

        .competitor-left::before {
            right: 0;
        }

        .competitor-right::before {
            left: 0;
        }

        /* Competitor Header */
        .competitor-header {
            text-align: center;
            margin-bottom: 40px;
            position: relative;
        }

        .competitor-logo {
            width: 80px;
            height: 80px;
            border-radius: 20px;
            margin: 0 auto 20px;
            display: flex;
            align-items: center;
            justify-content: center;
            font-size: 32px;
            font-weight: 900;
            border: 2px solid;
            position: relative;
        }

        .competitor-left .competitor-logo {
            background: linear-gradient(135deg, #00ffff, #0080ff);
            border-color: #00ffff;
            box-shadow: 0 0 30px rgba(0, 255, 255, 0.4);
        }

        .competitor-right .competitor-logo {
            background: linear-gradient(135deg, #ff0080, #ff4080);
            border-color: #ff0080;
            box-shadow: 0 0 30px rgba(255, 0, 128, 0.4);
        }

        .competitor-name {
            font-size: 28px;
            font-weight: 700;
            margin-bottom: 8px;
            text-transform: uppercase;
            letter-spacing: 2px;
        }

        .competitor-left .competitor-name {
            color: #00ffff;
            text-shadow: 0 0 10px rgba(0, 255, 255, 0.5);
        }

        .competitor-right .competitor-name {
            color: #ff0080;
            text-shadow: 0 0 10px rgba(255, 0, 128, 0.5);
        }

        .competitor-tagline {
            font-size: 12px;
            color: rgba(255, 255, 255, 0.6);
            text-transform: uppercase;
            letter-spacing: 1px;
        }

        /* Stats Grid */
        .stats-grid {
            display: grid;
            grid-template-columns: 1fr;
            gap: 25px;
            margin-bottom: 40px;
        }

        .stat-card {
            background: rgba(0, 0, 0, 0.3);
            border-radius: 15px;
            padding: 25px;
            border: 1px solid rgba(255, 255, 255, 0.1);
            position: relative;
            overflow: hidden;
            transition: transform 0.3s ease;
        }

        .stat-card:hover {
            transform: translateY(-5px);
        }

        .stat-card::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            height: 3px;
            border-radius: 15px 15px 0 0;
        }

        .competitor-left .stat-card::before {
            background: linear-gradient(90deg, #00ffff, #0080ff);
        }

        .competitor-right .stat-card::before {
            background: linear-gradient(90deg, #ff0080, #ff4080);
        }

        .stat-label {
            font-size: 12px;
            text-transform: uppercase;
            letter-spacing: 1px;
            color: rgba(255, 255, 255, 0.6);
            margin-bottom: 10px;
            font-weight: 500;
        }

        .stat-value {
            font-size: 32px;
            font-weight: 700;
            font-family: 'JetBrains Mono', monospace;
            margin-bottom: 8px;
        }

        .competitor-left .stat-value {
            color: #00ffff;
            text-shadow: 0 0 10px rgba(0, 255, 255, 0.3);
        }

        .competitor-right .stat-value {
            color: #ff0080;
            text-shadow: 0 0 10px rgba(255, 0, 128, 0.3);
        }

        .stat-trend {
            font-size: 11px;
            font-weight: 600;
            display: flex;
            align-items: center;
            gap: 5px;
        }

        .trend-up {
            color: #00ff88;
        }

        .trend-down {
            color: #ff4444;
        }

        .trend-neutral {
            color: #ffaa00;
        }

        /* Performance Bars */
        .performance-section {
            margin-bottom: 30px;
        }

        .section-title {
            font-size: 16px;
            font-weight: 600;
            text-transform: uppercase;
            letter-spacing: 1px;
            margin-bottom: 20px;
            color: rgba(255, 255, 255, 0.8);
            text-align: center;
            border-bottom: 1px solid rgba(255, 255, 255, 0.1);
            padding-bottom: 10px;
        }

        .performance-bars {
            display: flex;
            flex-direction: column;
            gap: 15px;
        }

        .performance-item {
            display: flex;
            flex-direction: column;
            gap: 8px;
        }

        .performance-label {
            font-size: 11px;
            text-transform: uppercase;
            letter-spacing: 1px;
            color: rgba(255, 255, 255, 0.6);
            display: flex;
            justify-content: space-between;
        }

        .performance-bar-bg {
            height: 8px;
            background: rgba(255, 255, 255, 0.1);
            border-radius: 4px;
            overflow: hidden;
            position: relative;
        }

        .performance-bar {
            height: 100%;
            border-radius: 4px;
            position: relative;
            animation: fillPerformance 1.5s ease-out;
        }

        .competitor-left .performance-bar {
            background: linear-gradient(90deg, #00ffff, #0080ff);
            box-shadow: 0 0 10px rgba(0, 255, 255, 0.4);
        }

        .competitor-right .performance-bar {
            background: linear-gradient(90deg, #ff0080, #ff4080);
            box-shadow: 0 0 10px rgba(255, 0, 128, 0.4);
        }

        /* Winner Badge */
        .winner-badge {
            position: absolute;
            top: 20px;
            right: 20px;
            background: linear-gradient(135deg, #00ff88, #00cc66);
            color: #0a0a0a;
            padding: 8px 15px;
            border-radius: 20px;
            font-size: 10px;
            font-weight: 700;
            text-transform: uppercase;
            letter-spacing: 1px;
            animation: pulse 2s ease-in-out infinite;
        }

        /* Market Share Indicator */
        .market-share {
            text-align: center;
            margin-bottom: 20px;
        }

        .share-circle {
            width: 100px;
            height: 100px;
            border-radius: 50%;
            margin: 0 auto 15px;
            position: relative;
            display: flex;
            align-items: center;
            justify-content: center;
            font-size: 18px;
            font-weight: 700;
            font-family: 'JetBrains Mono', monospace;
        }

        .competitor-left .share-circle {
            background: conic-gradient(#00ffff 0deg 140deg, rgba(255, 255, 255, 0.1) 140deg 360deg);
            color: #00ffff;
            border: 3px solid #00ffff;
        }

        .competitor-right .share-circle {
            background: conic-gradient(#ff0080 0deg 180deg, rgba(255, 255, 255, 0.1) 180deg 360deg);
            color: #ff0080;
            border: 3px solid #ff0080;
        }

        .share-label {
            font-size: 11px;
            color: rgba(255, 255, 255, 0.6);
            text-transform: uppercase;
            letter-spacing: 1px;
        }

        /* Battle Outcome */
        .battle-outcome {
            background: rgba(0, 0, 0, 0.8);
            backdrop-filter: blur(10px);
            padding: 20px;
            text-align: center;
            border-top: 1px solid rgba(255, 255, 255, 0.1);
            z-index: 20;
        }

        .outcome-title {
            font-size: 14px;
            color: rgba(255, 255, 255, 0.6);
            text-transform: uppercase;
            letter-spacing: 2px;
            margin-bottom: 10px;
        }

        .outcome-result {
            font-size: 18px;
            font-weight: 700;
            color: #00ff88;
            text-transform: uppercase;
            letter-spacing: 1px;
        }

        /* Animations */
        @keyframes gradientShift {
            0% { background-position: 0% 50%; }
            100% { background-position: 100% 50%; }
        }

        @keyframes fillPerformance {
            from { width: 0%; }
            to { width: var(--width); }
        }

        @keyframes pulse {
            0%, 100% { transform: scale(1); opacity: 1; }
            50% { transform: scale(1.05); opacity: 0.8; }
        }

        /* Cyber Glitch Effect */
        .glitch {
            position: relative;
        }

        .glitch::before,
        .glitch::after {
            content: attr(data-text);
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            opacity: 0.8;
        }

        .glitch::before {
            animation: glitch-1 2s infinite;
            color: #00ffff;
            z-index: -1;
        }

        .glitch::after {
            animation: glitch-2 2s infinite;
            color: #ff0080;
            z-index: -2;
        }

        @keyframes glitch-1 {
            0%, 14%, 15%, 49%, 50%, 99%, 100% { transform: translate(0); }
            15%, 49% { transform: translate(-2px, 0); }
        }

        @keyframes glitch-2 {
            0%, 20%, 21%, 62%, 63%, 99%, 100% { transform: translate(0); }
            21%, 62% { transform: translate(2px, 0); }
        }
    </style>
</head>
