            margin-bottom: 40px;
            position: relative;
            animation: slideIn 0.8s ease-out;
        }

        .tip-item:nth-child(even) {
            flex-direction: row-reverse;
        }

        .tip-item:last-child {
            margin-bottom: 0;
        }

        /* Tip Number Circle */
        .tip-number {
            position: absolute;
            left: 50%;
            transform: translateX(-50%);
            width: 60px;
            height: 60px;
            background: linear-gradient(135deg, #e53e3e, #ff6b6b);
