        }

        .tip-item:nth-child(even) .tip-content {
            margin-left: 80px;
            margin-right: 0;
        }

        /* Speech Bubble Arrow */
        .tip-content::before {
            content: '';
            position: absolute;
            top: 50%;
            right: -15px;
            transform: translateY(-50%);
            width: 0;
            height: 0;
            border-left: 15px solid rgba(255, 255, 255, 0.9);
            border-top: 15px solid transparent;
            border-bottom: 15px solid transparent;
        }

        .tip-item:nth-child(even) .tip-content::before {
            right: auto;
            left: -15px;
            border-left: none;
            border-right: 15px solid rgba(255, 255, 255, 0.9);
        }

        .tip-title {
